// TryEach
func TestInt64Slice_TryEach(t *testing.T) {

	var rabbit int64
	myErr := errors.New("i am an error")
	tests := []struct {
		name      string
		slice     []int64
		expected  int
		expected2 error
		before    func()
		eachFunc  func(int64) error
	}{
		{
			name:      "add n",
			slice:     []int64{1, 2, 5, 11, 13, 15},
			expected:  NotInSlice,
			expected2: nil,
			eachFunc: func(n int64) error {
				rabbit += n
				return nil
			},
		},
		{
			name:      "subtract n",
			slice:     []int64{1, 2, 6, 8, 12},
			expected:  NotInSlice,
			expected2: nil,
			eachFunc: func(n int64) error {
				rabbit -= n
				return nil
			},
		},
		{
			name:      "errors",
			slice:     []int64{1, 2, 5, 11, 13, 15},
			expected:  3,
			expected2: myErr,
			before:    func() { rabbit = 0 },
			eachFunc: func(n int64) error {
				if n > 5 {
					return myErr
				}
				rabbit += n
				return nil
			},
		},
	}

	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			if test.before != nil {
				test.before()
			}
			e, i := Int64Slice(test.slice).TryEach(test.eachFunc)
			if test.expected != e {
				t.Errorf("expected %v, got %v", test.expected, e)
			}
			if test.expected2 != i {
				t.Errorf("expected %v, got %v", test.expected2, i)
			}
		})
	}
}

