// Chunk
func TestInt64Slice_Chunk(t *testing.T) {
	tests := []struct {
		name     string
		size     int
		slice    []int64
		expected [][]int64
	}{
		{
			name:     "chunks of 2 no remainder",
			size:     2,
			slice:    []int64{1, 2, 5, 11, 13, 15},
			expected: [][]int64{[]int64{1, 2}, []int64{5, 11}, []int64{13, 15}},
		},
		{
			name:     "chunks of 2 with remainder",
			size:     2,
			slice:    []int64{1, 2, 5, 11, 13, 15, 17},
			expected: [][]int64{[]int64{1, 2}, []int64{5, 11}, []int64{13, 15}, []int64{17}},
		},
		{
			name:     "chunks of 100",
			size:     100,
			slice:    []int64{1, 2, 5, 11, 13, 15, 17},
			expected: [][]int64{[]int64{1, 2, 5, 11, 13, 15, 17}},
		},
		{
			name:     "chunks of 4",
			size:     4,
			slice:    []int64{1, 2, 5, 11, 13, 15, 17},
			expected: [][]int64{[]int64{1, 2, 5, 11}, []int64{13, 15, 17}},
		},
		{
			name:     "chunks of 5",
			size:     5,
			slice:    []int64{1, 2, 5, 11, 13, 15, 17},
			expected: [][]int64{[]int64{1, 2, 5, 11, 13}, []int64{15, 17}},
		},
		{
			name:     "empty slice",
			size:     5,
			slice:    []int64{},
			expected: [][]int64{},
		},
		{
			name:     "invalid chunk size",
			size:     -1,
			slice:    []int64{1, 2, 5, 11, 13, 15, 17},
			expected: [][]int64{},
		},
	}

	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			out := Int64Slice(test.slice).Chunk(test.size)
			if !reflect.DeepEqual(test.expected, out) {
				t.Errorf("expected %v, got %v", test.expected, test.slice)
			}
		})
	}
}

